mapper.select:
select i.iscrNo, m.id, m.name, m.phone1,m.phone2,m.phone3,i.status,i.regDate
from instructorInfo i
join member m on i.mebrNo = m.mebrNo;

mapper.update:
update instructorInfo
set intro = #{intro}
  ,status = #{status}
  ,hourPmt = #{hourPmt}
  ,etc = #{etc}
  ,modifyDate = now()
where iscrNo = #{iscrNo}
