    SET codeNum = #{codeNum}, title = #{title}, name=#{name}, startDate=#{startDate}, endDate=#{endDate}, startTime=#{startTime}, endTime=#{endTime}, price=#{price}, status=#{status}, onOff=#{onOff}, description=#{description}, location=#{location}, minNum=#{minNum}, maxNum=#{maxNum}, book=#{book}, material=#{material}, writer=#{writer}, regDate=#{regDate}, modifyDate=#{modifyDate}, etc=#{etc}
WHERE classEnrollNo = #{classEnrollNo} and writer = #{writer}
